(function() {
	if (current.user.company == '14828533db2bf740f4db16d15b961937') {
		var onMusicRec = [];
		var micLav = new GlideRecord('sys_user');
		micLav.get('sys_id','ac9677bedb46fb005e5783305b9619bc');
		onMusicRec.push(micLav.sys_id + ',' + current.user.sys_id);
		gs.eventQueue('x_ipegh_copyright.reminder', current, onMusicRec.toString());

	} 
	else {
	return current.user;
	}
}());

